/**************************************************
 * Dracula Theme
 * Copyright (c) 2007-2020 Postbox, Inc.
**************************************************/

/* Toolbars */
--toolbar-top: #282A36;
--toolbar-bottom: #282A36;
--toolbar-inactive: #282A36;

/* Tabs */
--toolbar-tab-active: #282A36;
--toolbar-tab-inactive: #1A1B23;

/* Toolbar Buttons */
--toolbar-button-default: rgba(255,255,255,0.17);
--toolbar-button-selected: rgba(255,255,255,0.25);
--toolbar-button-depressed: rgba(255,255,255,0.25);
--toolbar-button-border: transparent;

/* Favorites Bar Buttons */
--favbar-button-selected: #383B4A;
--favbar-button-hover: #44475A;

/* Backgrounds */
--bg-primary: #21232D;
--bg-secondary: #282A36;
--bg-sidebar: #282A36;

/* Dividers */
--divider-dark: #1e2029;
--divider-light: #17191f;

/* Selectors */
--selector: #44475A;
--selector-sidebar: #44475A;
--selector-inactive: #383B4A;
--selector-sidebar-inactive: #383B4A;
--selector-thread-inactive: #383B4A;
--selector-thread-child: #2C2E3A;

/* Fonts */
--font-primary: #C5CBDD;
--font-primary-sidebar: #C5CBDD;
--font-secondary: #7280ad;
--font-secondary-sidebar: #7280ad;
--font-primary-selected: #FFFFFF;
--font-secondary-selected: #a1aac8;
--font-pending: #455073;

/* Text Fields */
--text-field-bg: rgba(255,255,255,0.14);
--text-field-border: transparent;

/* Folder Pane Bubbles */
--folder-bubble-bg: #6272A4;
--folder-bubble-bg-selected: #6272A4;
--folder-bubble-bg-focus: #6272A4;
--folder-bubble-text: #EEF1F4;
--folder-bubble-text-selected: #EEF1F4;
--folder-bubble-text-focus: #FFFFFF;

/* Focus Pane Bubbles */
--focus-bubble-bg: #6272A4;
--focus-bubble-bg-selected: #6272A4;
--focus-bubble-bg-focus: #6272A4;
--focus-bubble-text: #EEF1F4;
--focus-bubble-text-selected: #EEF1F4;
--focus-bubble-text-focus: #FFFFFF;

/* Message List Bubbles */
--message-bubble-bg: #6272A4;
--message-bubble-bg-selected: #6272A4;
--message-bubble-bg-focus: #6272A4;
--message-bubble-text: #EEF1F4;
--message-bubble-text-selected: #EEF1F4;
--message-bubble-text-focus: #FFFFFF;

/* Notification Bars */
--notification-bg-blue: #3687D5;
--notification-text-blue: rgba(255,255,255,0.99);
--notification-bg-yellow: #DCB439;
--notification-text-yellow: rgba(255,255,255,0.99);
--notification-bg-red: #B33620;
--notification-text-red: rgba(255,255,255,0.99);
